contracts/Lottery.sol.content:
// SPDX-License-Identifier: GPL-3.0
//Author: Johnleouf21

pragma solidity 0.8.17;

import '@openzeppelin/contracts/token/ERC1155/ERC1155.sol';
import '@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol';
import '@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/access/AccessControlEnumerable.sol';
import '@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol';
import '@openzeppelin/contracts/utils/Strings.sol';

contract Lottery is ERC1155, VRFConsumerBaseV2, Ownable, AccessControlEnumerable, ERC1155Supply{

    using Strings for uint256;

    struct Trait {
        string category;
        uint quantity;
        bool set;
    }

    uint public max_mint_allowed = 50;
    uint public priceSale = 0.000000000000000001 ether;
    uint public maxEntries = 1101;

    mapping(address => uint) public amountEntries;
    mapping(uint256 => Trait) public traits;
    mapping(string => bool) public categoryValidation;
    mapping (uint => address) public winners;

    address[] public players;
    uint256[] public s_randomWords;
    uint[] public entries;
    string[] public categories;
    uint256 public categoryPointer; 

    event TraitAdded(uint256 indexed _traitId, string _category, uint _quantity);
    event TraitRemoved(uint256 indexed _traitId);
    event CategoryAdded(string _category);

    string baseURI;
    bool public lotteryClosed;

    VRFCoordinatorV2Interface COORDINATOR;

    // Your subscription ID.
    uint64 s_subscriptionId;

    // Polygon coordinator. For other networks,
    // see https://docs.chain.link/docs/vrf-contracts/#configurations
    // mainnet = 0xAE975071Be8F8eE67addBC1A82488F1C24858067
    address vrfCoordinator = 0x7a1BaC17Ccc5b313516C5E16fb24f7659aA5ebed;

    // The gas lane to use, which specifies the maximum gas price to bump to.
    // For a list of available gas lanes on each network,
    // see https://docs.chain.link/docs/vrf-contracts/#configurations
    // mainnet = 0xcc294a196eeeb44da2888d17c0625cc88d70d9760a69d58d853ba6581a9ab0cd
    bytes32 keyHash = 0x4b09e658ed251bcafeebbc69400383d49f344ace09b9576fe248bb02c003fe9f;

    // Depends on the number of requested values that you want sent to the
    // fulfillRandomWords() function. Storing each word costs about 20,000 gas,
    // so 100,000 is a safe default for this example contract. Test and adjust
    // this limit based on the network that you select, the size of the request,
    // and the processing of the callback request in the fulfillRandomWords()
    // function.
    uint32 callbackGasLimit = 100000;

    // The default is 3, but you can set this higher.
    uint16 requestConfirmations = 3;
    uint32 numWords =  1;
    uint256 public s_requestId;


    constructor(uint64 subscriptionId, string memory _baseURI)
    VRFConsumerBaseV2(vrfCoordinator)
    ERC1155("NFSQUAT"){
        COORDINATOR = VRFCoordinatorV2Interface(vrfCoordinator);
        s_subscriptionId = subscriptionId;
        baseURI = _baseURI;
    }

    function getQuantity(uint256 tokenID) public view returns (uint) {
        require(traits[tokenID].set, "getTrait: trait not set");
        return traits[tokenID].quantity;         
    }

    function getTrait(uint256 tokenID) public view returns (string memory, uint) {
        require(traits[tokenID].set, "getTrait: trait not set");
        return ( 
            traits[tokenID].category,
            traits[tokenID].quantity
        );         
    }

    function getCategories() public view returns (string[] memory) {
        return categories;
    }

    function addTrait(
        uint256 _tokenID, 
        string calldata _category,
        uint _quantity
    ) 
        public 
        onlyOwner 
    {
        require(!traits[_tokenID].set, "addTrait: Trait ID already set");
        require(categoryValidation[_category], "addTrait: Category not valid");

        traits[_tokenID] = Trait({
            category: _category,
            quantity: _quantity,
            set: true
        });

        emit TraitAdded(_tokenID, _category, _quantity);
    }

    function addCategory(string memory _category) public onlyOwner {
        require(!categoryValidation[_category], "addCategory: Category already added");
        categoryValidation[_category] = true;
        categories.push(_category);
        categoryPointer++;
        emit CategoryAdded(_category);
    }

    function removeTrait(uint256 _tokenID) public  onlyOwner {
        require(traits[_tokenID].set, "removeTrait: Trait ID not set");
        require(totalSupply(_tokenID)==0, "removeTrait: Trait ID already minted");
        delete(traits[_tokenID]);
        emit TraitRemoved(_tokenID);
    }

    function requestRandomWords() external onlyOwner {
        require(lotteryClosed, "Lottery is not closed");
        s_requestId = COORDINATOR.requestRandomWords(
        keyHash,
        s_subscriptionId,
        requestConfirmations,
        callbackGasLimit,
        numWords
        );
    }

    function fulfillRandomWords(
        uint256, 
        uint256[] memory randomWords
    ) internal override {
        s_randomWords = randomWords;
    }

    function toggleLottery() external onlyOwner {
        lotteryClosed = !lotteryClosed;
    }

    function pickWinner(uint tokenID) public {
        require(traits[tokenID].set, "getTrait: trait not set");
        require(lotteryClosed, "Lottery is not closed");
        uint winner = s_randomWords[0] % players.length;
        _mint(players[winner], tokenID, 1,'');
        traits[tokenID].quantity--;
        players[winner] = players[players.length - 1];
        players.pop();     
    }

    function enter() external payable {
        require(!lotteryClosed, "Lottery is closed");
        uint price = priceSale; 
        require(msg.value >= price, "Not enought funds.");
        require(amountEntries[msg.sender] + 1 <= max_mint_allowed, "You can't enter more than X time");
        require(entries.length <= maxEntries, "Number of remaining entries is insufficient");
        players.push(msg.sender);
        amountEntries[msg.sender] + 1;
        if(entries.length == maxEntries) {
            lotteryClosed = true;
        }
    }

    function uri(uint256 tokenID) public view virtual override returns (string memory) {
        return string(abi.encodePacked(baseURI,tokenID.toString(),".json"));
    }

    function _beforeTokenTransfer(
        address operator, 
        address from, 
        address to, 
        uint256[] memory ids, 
        uint256[] memory amounts, 
        bytes memory data
    )
        internal
        override(ERC1155, ERC1155Supply)
    {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155, AccessControlEnumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

      function withdraw() external onlyOwner {
        uint256 balance = address(this).balance; // get the balance of the smart contract
        payable(msg.sender).transfer(balance);
    }

}

